import React from 'react'
import { Button } from 'react-native'
import { ScrollView } from 'react-native'
import { View, Text,StyleSheet } from 'react-native'
import { TheContextConsumer } from '../context/context'


export default function UsersList({navigation}) {
    

    return (
        <TheContextConsumer >
            {(value)=>{
                return(
                    <View style={styles.container}>
                        <Text></Text>
                        <View style={{height:70,alignItems:"center",justifyContent:"center",backgroundColor:"lightgrey",width:"100%"}}>
                            <Text > Liste des utilisateurs </Text>
                            
                        </View>
                        

                        <ScrollView style={{backgroundColor:"grey",width:"100%",paddingTop:10}}>
                            
                            {(value.usersList) && value.usersList.map((user,i)=>{

                                return(
                                    <View key={i} style={styles.userCard}>
                                        <Text style={{margin:5,backgroundColor:"red"}}>
                                            {user.prenom+" "}
                                            {user.nom}
                                            
                                        </Text>
                                    </View>
                                )
                            })}
                        </ScrollView>
                        <View style={{padding:20}}>
                                <Button
                                    title="Back"
                                />    
                        </View>
                        {/* <View style={{height:120,alignItems:"center",justifyContent:"space-between",backgroundColor:"lightgrey",width:"100%",display:"flex",flexDirection:"row"}}>
                            <View style={{padding:20}}>
                                <Button
                                    title="Back"
                                />    
                            </View>
                            <View style={{padding:20}}>
                                <Button
                                    title="Back"
                                />    
                            </View>
                            
                            
                        </View> */}
                    </View>            

                )
            }}
        </TheContextConsumer>

        
    )
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'space-between',
    },
    userCard:{
        height:70,
        backgroundColor:"lightgrey",
        marginVertical:10,
        marginHorizontal:10,
        borderRadius: 10
    }
});
