import React, { useEffect, useState } from 'react'
import { StyleSheet,View, Text, Button } from 'react-native'
import { TheContextConsumer } from '../context/context';
import DateTimePicker from '@react-native-community/datetimepicker';
import { TextInput } from 'react-native-gesture-handler';
import { fireStore } from '../firebase/firebase';

export default function Home({ navigation }) {
    const [date, setDate] = useState(new Date(1598051730000));
    const [mode, setMode] = useState('date');
    const [show, setShow] = useState(false);
    const handleOnChange = (event, selectedDate) => {
        const currentDate = selectedDate || date;
        setShow(Platform.OS === 'ios');
        setDate(currentDate);
    };

    const showMode = (currentMode) => {
    setShow(true);
    setMode(currentMode);
    };

    const showDatepicker = () => {
    showMode('date');
    };

    const LogTheDate = (e) => {
        fireStore.collection('testDate').get().then((snapshot)=>{
            snapshot.docs.map((doc)=>{
                console.log("TimeStamp to date",new Date(doc.data().date.seconds))
            })
        })
        fireStore.collection('testDate').add({
            date : date
        })
        console.log(date)
    
    };
    return (

        <View style={styles.container}>
            <View style={{backgroundColor:"red"}} onPress={(e)=>{console.log('test')}}>
                <Text>click on me</Text>
            </View>
            <View>
                <View>
                    <Button onPress={showDatepicker} title="Show date picker!" />
                </View>
                <View>
                    <Button onPress={LogTheDate} title="log date!" />
                </View>
                <TextInput
                    style={styles.input}
                    placeholder="Date" 
                    label="Date" 
                    value={date}
                    onChangeText={(text) => {setuser({...user,email:text})}}
                    onPress={showDatepicker}
                />
                {show && (
                <DateTimePicker
                    testID="dateTimePicker"
                    value={date}
                    mode={mode}
                    is24Hour={true}
                    display="default"
                    onChange={handleOnChange}
                />
                )}
            </View>
            {/* <Text>Home Page</Text> */}
            
            <Text style={{textAlign:"center",padding:10,fontSize:20}}>

                Bienvenu!{"\n"}{"\n"}
                Nous avons besoin de votre aide pour ajouter les comptes des locataires {"\n"}{"\n"}{"\n"}{"\n"}
                <Text style={{textAlign:"center",padding:10,fontSize:60}}> ðŸ‘· </Text>
            </Text>
            <Button
                title="Commencer"
                onPress={() =>
                    navigation.navigate('Login')
                }
            />
            <View></View>
        </View>
    )
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'space-between',
    },
  });
