import React from 'react'
import { Alert } from 'react-native'
import { Button } from 'react-native'
import { View, Text,StyleSheet } from 'react-native'
import { TheContextConsumer } from '../context/context'

export default function Profile({navigation}) {

    const FindNextJun = (date)=>{
        let dateToSec = date.getTime()
        let junDateToSec = date.setMonth(5)
        junDateToSec = date.setDate(1)
        if(dateToSec-junDateToSec > 0){
            return(new Date(365*24*60*60*1000+junDateToSec))
        }else{
            return(new Date(junDateToSec))
        }
    }
    const chargeParAnnee = (Appartement) => {
        let dayPrice = 4850/365;
        let today = new Date()
        today.setUTCHours(0, 0, 0, 0);
        let NextJun = FindNextJun(today)
        let x = (Math.floor( dayPrice*(NextJun.getTime()-Appartement.DateDeSignatureDuContrat.toDate().getTime())/(1000*60*60*24) ))

        if(x >= 4850){
            while(true){
                x = x-4850
                console.log(x)
                if(x<4850){
                    break
                }
            }            
        }else{
            console.log(x)
        }

        // console.log("fin : ",x)
        // if(some >= 4850){
        //     some = some-4850
        //     Alert.alert(some.toString())
        // }else{
        //     Alert.alert(some.toString())
        //     bool = false
        // }
    }
    const ChargeByAppartement = (userToAdd) => {
        
        return(

            <View>
                {userToAdd.proprietaireDe.map((Appartement,i)=>{
                    chargeParAnnee(Appartement)
                    let today = new Date()
                    today.setUTCHours(0, 0, 0, 0);
                    let NextJun = FindNextJun(today)
                    let dayPrice = 4850/365
                    let x = (Math.floor( dayPrice*(NextJun.getTime()-Appartement.DateDeSignatureDuContrat.toDate().getTime())/(1000*60*60*24) ))
                    return(
                        <Text style={{textAlign:"center"}}>
                            {"appart " + (Appartement.titreFoncier) + " : "}
                            {"Montant à payer : " + JSON.stringify(  x  )+" DH " + "\n"}
                        </Text>
                    )
                })} 
            </View>
        )
    }
    
    const charges = (ListDesAppartements) => {
        let today = new Date()
        today.setUTCHours(0, 0, 0, 0);
        let ToDayNextJun = FindNextJun(today)
        let dayPrice = 4850/365
        ListDesAppartements.map((appartment,i)=>{
            let ContractTimeStamp = appartment.DateDeSignatureDuContrat.toDate().getTime()
            let nextJuneTimeStamp = FindNextJun(appartment.DateDeSignatureDuContrat.toDate()).getTime()
            let Diff = nextJuneTimeStamp-ContractTimeStamp
            Alert.alert("",
                "avant    "+JSON.stringify((new Date(nextJuneTimeStamp-(365*1000*60*60*24))))+"\n"+
                
                "contrat "+JSON.stringify(ContractTimeStamp)+"\n"
                +
                "suivant "+JSON.stringify(nextJuneTimeStamp)+"\n"

                +JSON.stringify(  Math.floor( (Diff/(1000*60*60*24))*dayPrice )  )+" Dh "+"\n"
                + "à payer Pour la saison : "
                +"\n"+
                "2020"
            )
                
                 
                // + 
                // (JSON.stringify(FindNextJun(dateDuContrat).getTime()-dateDuContrat.getTime())) + " jours")


            // Alert.alert(
            //     "",
            //     JSON.stringify(appartment.DateDeSignatureDuContrat.toDate().getTime())
            //     +"\n"
            //     + ToDayNextJun.getTime()
                
            // )

            
        })
    }


    

    return (
        <TheContextConsumer>
                {(value) => {

                    
                    return(
                        <View style={styles.container}>
                            <View></View>
                            <Text>Profile</Text>

                            <View>
                                {charges(value.userState.proprietaireDe)}
                            </View>
                            <View>
                                <Button
                                    title="back"
                                    onPress={()=>{
                                        navigation.navigate("Dashboard")
                                    }}
                                />
                            </View>
                        </View>

                    )
                }}
        </TheContextConsumer>        
    )
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'space-around',
    },
  });
