import React, { useState,useEffect  } from 'react'
import { Alert } from 'react-native';
import { Button } from 'react-native';
import { Image,View, Text,StyleSheet } from 'react-native'
import * as ImagePicker from 'expo-image-picker';

export default function UploadReceipt({ route,navigation }) {
    const [input, setinput] = useState(route.params);
    const [image, setImage] = useState(null);
    const [file, setfile] = useState(null)
    useEffect(() => {
        (async () => {
          if (Platform.OS !== 'web') {
            const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
            if (status !== 'granted') {
              alert('Sorry, we need camera roll permissions to make this work!');
            }
          }
        })();
    }, []);
    const pickImage = async () => {
        let result = await ImagePicker.launchImageLibraryAsync({
          mediaTypes: ImagePicker.MediaTypeOptions.Images,
          allowsEditing: true,
          aspect: [4, 4],
          quality: 1,
        });

        if (!result.cancelled) {
          setImage(result.uri);
          setfile(result)
          
        }
    };
    return (
        <View style={styles.container}>
            <Text></Text>
            <View style={{height:70,alignItems:"center",justifyContent:"center",backgroundColor:"lightgrey",width:"100%"}}>
                <Text> Profile </Text>
            </View>
            <View style={{flex:1,justifyContent:"space-between"}}>
                <View style={{alignItems:"center"}}>
                    <Text>
                        Propriétaire : {input.user.nom+" "+input.user.prenom} 
                    </Text>
                    <Text>
                        Immeuble : {input.appartement.Immeuble}
                    </Text>
                    <Text>
                        N° : {input.appartement.NAppartement}
                    </Text>
                </View>


                <View style={{backgroundColor:"red",alignItems:"center"}}>
                    {/* <Text>
                        choose a file
                    </Text> */}
                    <Image source={{ uri: image }} style={styles.logo} />
                    <Button
                        title="select an image"
                        onPress={()=>{
                            pickImage()
                        }}
                    /> 
                    <Text>{JSON.stringify(file)} </Text>
                </View>



                <Button
                    title="ajouter un reçu"
                    onPress={()=>{
                        Alert.alert("get ready")
                        // navigation.navigate("Profile")
                    }}
                />    
            </View>
            
            <View style={{backgroundColor:"black",width:"100%",display:"flex",flexDirection:"row",alignItems:"center",justifyContent:"center"}}>
                <View style={{padding:20,paddingHorizontal:50}}>
                    <Button
                        title="back"
                        onPress={()=>{
                            navigation.navigate("Profile")
                        }}
                    />    
                </View>
                
                
            </View>
            <Text></Text>
        </View>
    )
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'space-around',
    },
    logo : {
        width:200,
        minHeight:200,
        borderWidth:5,
        borderColor:"black"
    }
  });
