import React from 'react'
import { Alert } from 'react-native'
import { Button } from 'react-native'
import { View, Text,StyleSheet } from 'react-native'
import { TheContextConsumer } from '../context/context'

export default function Profile({navigation}) {

    const FindNextJun = (date)=>{
        let dateToSec = date.getTime()
        let junDateToSec = date.setMonth(5)
        junDateToSec = date.setDate(1)
        if(dateToSec-junDateToSec > 0){
            return(new Date(365*24*60*60*1000+junDateToSec))
        }else{
            return(new Date(junDateToSec))
        }
    }
    const chargeParAnnee = (Appartement) => {
        let dayPrice = 4850/365;
        let today = new Date()
        today.setUTCHours(0, 0, 0, 0);
        let NextJun = FindNextJun(today)
        let x = (Math.floor( dayPrice*(NextJun.getTime()-Appartement.DateDeSignatureDuContrat.toDate().getTime())/(1000*60*60*24) ))

        if(x >= 4850){
            while(true){
                x = x-4850
                console.log(x)
                if(x<4850){
                    break
                }
            }            
        }else{
            console.log(x)
        }

        // console.log("fin : ",x)
        // if(some >= 4850){
        //     some = some-4850
        //     Alert.alert(some.toString())
        // }else{
        //     Alert.alert(some.toString())
        //     bool = false
        // }
    }
    const ChargeByAppartement = (userToAdd) => {
        
        return(

            <View>
                {userToAdd.proprietaireDe.map((Appartement,i)=>{
                    chargeParAnnee(Appartement)
                    let today = new Date()
                    today.setUTCHours(0, 0, 0, 0);
                    let NextJun = FindNextJun(today)
                    let dayPrice = 4850/365
                    let x = (Math.floor( dayPrice*(NextJun.getTime()-Appartement.DateDeSignatureDuContrat.toDate().getTime())/(1000*60*60*24) ))
                    return(
                        <Text style={{textAlign:"center"}}>
                            {"appart " + (Appartement.titreFoncier) + " : "}
                            {"Montant à payer : " + JSON.stringify(  x  )+" DH " + "\n"}
                        </Text>
                    )
                })} 
            </View>
        )
    }
    
    const charges = (ListDesAppartements) => {
        let results = [
            
        ]

        let today = new Date()
        today.setUTCHours(0, 0, 0, 0);
        let NextJunFromToDay = FindNextJun(today)
        let dayPrice = 4850/365

        ListDesAppartements.map((appartment,i)=>{
            
            let ContractTimeStamp = appartment.DateDeSignatureDuContrat.toDate().getTime()
            let ContractnextJuneTimeStamp = FindNextJun(appartment.DateDeSignatureDuContrat.toDate()).getTime()

            // let DiffContractJune = ContractnextJuneTimeStamp-ContractTimeStamp

            // let june1 = (new Date(ContractnextJuneTimeStamp-(365*1000*60*60*24))).getFullYear()
            // let june2 = (new Date(ContractnextJuneTimeStamp)).getFullYear()


            

            if(ContractnextJuneTimeStamp === NextJunFromToDay.getTime()){
                results.push({
                    jours : Math.floor( ((NextJunFromToDay.getTime()-ContractnextJuneTimeStamp)/(100*60*60*24)) )
                })
                // Alert.alert("",
                //     "c nxt : "+ContractnextJuneTimeStamp
                //     +"\n"+
                //     "today nxt : "+NextJunFromToDay.getTime()+"&&&&&&&&&&&"
                // )
            }else{

                    // Alert.alert("",
                    //     "c nxt : "+ContractnextJuneTimeStamp
                    //     +"\n"+"aaaaaaaaaaa"+
                    //     "today nxt : "+NextJunFromToDay.getTime()
                    // )
                    // ContractnextJuneTimeStamp = ContractnextJuneTimeStamp + 1000*60*60*24*356
                    // Alert.alert("",
                    //     "c nxt : "+ContractnextJuneTimeStamp
                    //     +"\n"+
                    //     "today nxt : "+NextJunFromToDay.getTime()+"aaaaaaa"
                    // )
                    // ContractnextJuneTimeStamp = ContractnextJuneTimeStamp + 1000*60*60*24*356

                    results.push({
                        jours : Math.floor( ((NextJunFromToDay.getTime())) )
                    })
                
            }
            Alert.alert("",
                JSON.stringify(results)
            )
            // Alert.alert("",
            //     JSON.stringify((new Date(ContractTimeStamp)))
            //     +
            //     "\n"
            //     +
            //     JSON.stringify((new Date(ContractnextJuneTimeStamp+3*(1000*60*60*24*365))))
            //     +
            //     "\n"
            //     +
            //     JSON.stringify((new Date(NextJunFromToDay.getTime())))
            //     +
            //     "\n"
            //     +
            //     JSON.stringify(  Math.floor( (DiffContractJune/(1000*60*60*24))*dayPrice )  )+" Dh "+"\n"
            //     + "à payer Pour la saison : "
            //     +"\n"+
            //     +june1+"-"+june2
                
            // )
            

            
        })
    }


    

    return (
        <TheContextConsumer>
                {(value) => {

                    
                    return(
                        <View style={styles.container}>
                            <View></View>
                            <Text>Profile</Text>
                            
                            <View>
                                
                                {charges(value.userState.proprietaireDe)}
                            </View>
                            <View>
                                <Button
                                    title="back"
                                    onPress={()=>{
                                        navigation.navigate("Dashboard")
                                    }}
                                />
                            </View>
                        </View>

                    )
                }}
        </TheContextConsumer>        
    )
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'space-around',
    },
  });
