import React, { useEffect, useState } from 'react'
import { Modal,ScrollView,Button } from 'react-native';

import { View, Text, StyleSheet } from 'react-native'
import {  TouchableOpacity } from 'react-native-gesture-handler';
import { AppCharges } from '../useFull/Charges';

const listToRows = (Users) =>{
    let List = [];
    Users.map((user) => {
        user["proprietaireDe"].map((appartement) => {
            let o = AppCharges(appartement);
            List.push({
                Immeuble : appartement.Immeuble,
                NAppartement:appartement.NAppartement,
                nom:user.nom,
                prenom:user.prenom,
                Charges : o.Charges,
                Total:o.Total, 
            })
        })
    })
    return(List)
}


export default function AppList(props) {
    const [List, setList] = useState([])
    const [selectedAppartment, setselectedAppartment] = useState({
        nom:"",
        prenom:"",
        NAppartement:"",
        Immeuble:"",
        Charges:[]
    })
    const [showModal, setshowModal] = useState(false);


    useEffect(() => {
        setList(listToRows(props.Users))
    }, [])
    return (
        <View >
                    

            {(List) && List.map((row,index) => {
                
                
                return(
                    <TouchableOpacity 
                        key={index} 
                        style={styles.userCard} 
                        onPress={()=>{
                            setselectedAppartment(row)
                            setshowModal(true)
                        }}
                        >
                        <Text style={styles.column}>
                            {row.Immeuble}
                        </Text>
                        <Text style={styles.column}>
                            {row.NAppartement}
                        </Text>
                        <Text style={styles.column}>
                            {row.Total+"dh"}
                        </Text>
                        

                        
                    </TouchableOpacity>
                        
                    
                )
            })}
            <Modal           
                animationType = {"fade"}  
                transparent = {true}  
                visible = {showModal}  
                onRequestClose = {() =>{ setshowModal(false);setselectedAppartment({}) } }>  

                <View style={{width:"100%",height:"100%",alignItems:"center",justifyContent:"center"}}>
                    <View style = {styles.modal}>  
                        
                        <ScrollView style={{backgroundColor:"grey",width:"100%",paddingTop:10,borderRadius:10}}>
                            <View style={{padding:5}}>
                                <Text>
                                    {"Propriétaire : " + selectedAppartment.nom +" " +selectedAppartment.prenom}
                                </Text>
                                <Text>
                                    {"N°Appartement : " + selectedAppartment.NAppartement}
                                </Text>
                                <Text>
                                    {"Immeuble : " + selectedAppartment.Immeuble}
                                </Text>
                                <View style={{width:"100%",alignItems:"center"}} >
                                    <Text style={{textAlign:"center"}}>Total à payer depuis la date de signature du contrat {"\n"} {selectedAppartment.Total} DH </Text>
                                

                                {selectedAppartment.Charges.map((charge,i)=>{

                                    return(
                                        <View key={i}  style={{width:"100%",backgroundColor:"lightgrey",alignItems:"center",margin:10,borderRadius:5}}>
                                            
                                            
                                            <Text>
                                                {charge.startingDay+" --> "+charge.To+" ( "+charge.saison+" )"}
                                            </Text>

                                            <Text>
                                                {charge.days+" ( jours )"}
                                            </Text>
                                            <Text>
                                                {charge.charge+" ( Dh )"}
                                            </Text>

                                        </View>
                                    )
                                })}

                                </View>
                            </View>
                            
                        </ScrollView>
                        

                        <View style={{display:"flex",flexDirection:"row",justifyContent:"space-between"}}>
                            <View style={{margin:10}}>
                                <Button 
                                    title="Annuler"
                                    
                                    onPress = {() => {
                                        setshowModal(!showModal)
                                    }}
                                />
                            </View>
                            
                            
                        </View>
                        

                    </View>
                </View>  
            </Modal>
            
        </View>
    )
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'space-between',
    },
    userCard:{
        height:50,
        backgroundColor:"lightgrey",
        padding:10,
        marginVertical:10,
        marginHorizontal:10,
        borderRadius: 10,
        display:"flex",
        flexDirection:"row",
        alignItems:"center",
        justifyContent:"space-between"

    },
    row:{
        width:"100%",
        padding:10,
        marginHorizontal:10,
        // paddingHorizontal:40,
        display:"flex",
        flexDirection:"row",
        justifyContent:"space-between",
        backgroundColor:"red"
    },
    column :{
        fontSize:20,
        textAlign:"center",
        overflow: "hidden",
        flex:1,
        margin:1,
        borderWidth: 1,  
        borderColor: '#000',
        // backgroundColor:"yellow",

    },
    modal: {  
        justifyContent: 'space-around',  
        alignItems: 'center',   
        backgroundColor : "grey",   
        height: "70%" ,  
        width: '90%',  
        borderRadius:10,  
        borderWidth: 1,  
        borderColor: '#fff',    
        // marginTop: 80,  
        
        

    },
    text: {  
        color: 'black',  
        marginTop: 10,
        textAlign:"center",
        fontSize:20
    }    
});
