import React from 'react'
import { View, Text,StyleSheet, Button } from 'react-native'
import { TheContextConsumer } from '../context/context'
import { fireStore } from '../firebase/firebase'

export default function Dashboard({ navigation }) {
    
    
    return (
        <TheContextConsumer >
            {(value) => {
                    
                return(
                    <View style={styles.container}>
                        <View>
                            
                        </View>
                        
                        {(value.userState.isAdmin === !true) ? (<Text>admin</Text>) : (<Text>{"Bonjour "+ value.userState.prenom+" "+value.userState.nom}</Text>)} 
                        
                        <Button
                            title="profile"
                            onPress={  ()=>{
                                
                                navigation.navigate("Profile")
                                
                            }}
                        />
                        <View style={{backgroundColor:"yellow",width:"100%",padding:40,display:"flex",flexDirection:"row",justifyContent:"space-between"}}>

                        {(value.userState.isAdmin === true) &&   
                            <View>
                                <Button
                                    title="Ajouter un compte"
                                    onPress={() =>
                                        navigation.navigate('AddUser')
                                    }
                                />
                                <Button
                                    title="Modifier un compte"
                                    onPress={() =>
                                        {
                                            navigation.navigate('SelectUser');
                                            
                                        }
                                    }
                                />
                            </View>
                            
                        }
                            
                            <Button
                                
                                title="Data"
                                onPress={() =>
                                    navigation.navigate('DATA')
                                }
                            />

                        </View>
                        <Button
                            title="CO-PROPRIÃ‰TAIRES"
                            onPress={() => {
                                navigation.navigate('UsersList')
                                }
                            }
                        />
                        <Button
                            title="Logout"
                            onPress={() => {
                                value.LogOut();
                                navigation.navigate('Home')
                                }
                            }
                        />
                    </View>
                    )
                }
            }
        </TheContextConsumer>
        
    )
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'space-between',
    },
});
