import React from 'react'
import { View, Text,StyleSheet, Button } from 'react-native'
import { TheContextConsumer } from '../context/context'
import { fireStore } from '../firebase/firebase'

export default function Dashboard({ navigation }) {
    
    
    return (
        <TheContextConsumer >
            {(value) => {
                    
                return(
                    <View style={styles.container}>
                        <View>
                            
                        </View>
                        
                        
                        <Text>{"Bonjour "+ value.userState.prenom+" "+value.userState.nom} {JSON.stringify(value.userState.role)}</Text>
                        <Button
                            title="profile"
                            onPress={  ()=>{
                                
                                navigation.navigate("Profile")
                                
                            }}
                        />
                        <Button
                            title="CO-PROPRIÉTAIRES"
                            onPress={() => {
                                navigation.navigate('Appartments')
                                }
                            }
                        />
                        
                        {(value.userState.role === "Admin" || value.userState.role === "representative" ) && (
                            <Button
                                title="Ajouter un compte"
                                onPress={() =>
                                    navigation.navigate('AddUser')
                                }
                            />
                        )}
                        {/* {(value.userState.role === "User" || value.userState.role === "representative" ) && (
                            <Button
                                title="Modifier un compte"
                                onPress={() =>
                                    {
                                        navigation.navigate('SelectUser');
                                        
                                    }
                                }
                            />
                        )} */}
                        
                        
                        <Button
                            title="Logout"
                            onPress={() => {
                                value.LogOut();
                                navigation.navigate('Home')
                                }
                            }
                        />

                        <View>
                            
                        </View>
                    </View>
                    )
                }
            }
        </TheContextConsumer>
        
    )
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'space-between',
    },
});
