import React,{useState,useEffect} from 'react'
import { Alert } from 'react-native'
import { Button } from 'react-native'
import { View, Text,StyleSheet } from 'react-native'
import { ScrollView } from 'react-native-gesture-handler'
import AppList from '../components/AppList'
import ChargesList from '../components/ChargesList'
import { TheContextConsumer } from '../context/context'
import Charges from '../useFull/Charges'

export default function Profile({navigation}) {

    

    return (
        <TheContextConsumer>
                {(value) => {

                    
                    return(
                        <View style={styles.container}>
                            <Text></Text>
                            <View style={{height:70,alignItems:"center",justifyContent:"center",backgroundColor:"lightgrey",width:"100%"}}>
                                <Text> Profile </Text>
                            </View>
                            <ScrollView style={{backgroundColor:"back",width:"100%"}}>
                                <View>
                                    <ChargesList Appartements={value.userState.proprietaireDe}/>
                                </View>
                                <AppList Users={value.usersList}/>

                            </ScrollView>
                            
                            
                            <View style={{backgroundColor:"black",width:"100%",display:"flex",flexDirection:"row",alignItems:"center",justifyContent:"center"}}>
                                <View style={{padding:20,paddingHorizontal:50}}>
                                    <Button
                                        title="back"
                                        onPress={()=>{
                                            navigation.navigate("Dashboard")
                                        }}
                                    />    
                                </View>
                                <View style={{padding:20,paddingHorizontal:50}}>
                                    <Button
                                        title="ajouter un reÃ§u"
                                        onPress={()=>{
                                            navigation.navigate("Dashboard")
                                        }}
                                    />    
                                </View>
                                
                            </View>
                            <Text></Text>
                        </View>

                    )
                }}
        </TheContextConsumer>        
    )
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'space-around',
    },
  });
