import React, { useRef, useState } from 'react'
import { StyleSheet,View, Text, Button, Alert, ScrollView } from 'react-native'
import { TextInput, TouchableOpacity } from 'react-native-gesture-handler'

import { TheContextConsumer } from '../context/context';
import DateTimePicker from '@react-native-community/datetimepicker';
import { auThentication, fireStore } from '../firebase/firebase';
export default function AddUser({ navigation }) {
    // Usr Infos 
    const [userToAdd, setuserToAdd] = useState({
        email:"",
        password:"123456",
        nom:"",
        prenom:"",
        numeroDeTelephone:"",
        cin:"",
        
        proprietaireDe:[]
    })
    //++++++++++++++++++++++++++++++++++++++++++++++++ 

    // add appartement to appatements
    const handleAddAppartement = () => {
        setuserToAdd({...userToAdd,...userToAdd.proprietaireDe.push(
            {
                titreFoncier:"",
                DateDeSignatureDuContrat : "",
                NomDuNotaire : "",
                NAppartement : "",
                Immeuble : "",
            }
        )})
    }
    
    // ++++++++++++++++++++++++++++


    // Triggers to show date-pickers
    const [role, setrole] = useState(-1)
    //+++++++++++++++++++++++++++++

    // input Refs To focus on next input automatically
    const myRefs= useRef([]);
    const myRefs2= useRef([]);
    // +++++++++++++++++++++++++++++++++++++++++++++++

    // date picker parameters and functions
    const [mode, setMode] = useState('date');
    const [show, setShow] = useState(false);

    const handleDatePickerOnChange = (selectedDate,index,key) => {

        // let mySelectedDate = new Date(selectedDate)
        // Alert.alert(JSON.stringify(selectedDate),mySelectedDate.getFullYear()+" / "+mySelectedDate.getMonth()+" / "+mySelectedDate.getDate()+"\n"+(new Date(JSON.stringify(selectedDate).split('"')[1].split('T')[0])).toString())
        // let NewSelectedDate = new Date(mySelectedDate.getFullYear(),mySelectedDate.getMonth(),mySelectedDate.getDate());
        let NewObject = {...userToAdd}
        NewObject["proprietaireDe"][index][key] = (new Date(JSON.stringify(selectedDate).split('"')[1].split('T')[0]));
        return(NewObject)
    }
    const showMode = (currentMode) => {
        setMode(currentMode);
    };
    const showDatepicker = (x) => {
        showMode('date');
        setrole(x)
    };
    // +++++++++++++++++++++++++++++++++++++++++++++++
    const FindNextJun = (date)=>{
        let dateToSec = date.getTime()
        let junDateToSec = date.setMonth(5)
        junDateToSec = date.setDate(1)
        if(dateToSec-junDateToSec > 0){
            return(new Date(365*24*60*60*1000+junDateToSec))
        }else{
            return(new Date(junDateToSec))
        }
    }
    const ChargeByAppartement = () => {
        let newObject = {...userToAdd}
        let montantTotal = 0
        newObject.proprietaireDe.map((Appartement,i) =>{
            
            
            let today = new Date()
            today.setHours(0, 0, 0, 0);
            
            Alert.alert(JSON.stringify(FindNextJun(Appartement.DateDeSignatureDuContrat)),JSON.stringify(today)+"\n"+JSON.stringify(Math.floor(((FindNextJun(today)-Appartement.DateDeSignatureDuContrat)/(1000*60*60*24))*(4850/365)))+" dh")
        })
    }
    
    

    
    
    
    
    const handleSubmit = () => {
        let verfied = true;
        
        if(!userToAdd["proprietaireDe"][0]){
            verfied = false;
            Alert.alert("Champ maquant:","Veuillez ajouter au moins un appartement."+userToAdd["proprietaireDe"].length.toString())
        }

        Object.keys(userToAdd).map((key,i)=>{
            if(key.toString() === "proprietaireDe"){
                
                userToAdd[key].map((appartement,appI)=>{
                    // appartement["titreFoncier"]

                    Object.keys(appartement).map((appKey)=>{

                        if(appartement[appKey] === ""){
                            Alert.alert("Champ manquant:", appKey.toString() +"\n"+ "(Appartement : " + (appI+1)+")")
                            verfied = false
                        }
                        
                    })
                    for(var t = appI+1 ; t < userToAdd[key].length ; t++){
                        
                        if(appartement["titreFoncier"] === userToAdd[key][t]["titreFoncier"] ){
                            Alert.alert("Meme titre foncier pour deux appartement !!!","Appartement :"+(appI+1).toString()+" et "+(t+1).toString());
                            verfied = false
                            
                        }
                    }
                })
                
            }else{
                if(userToAdd[key] === ""){
                    Alert.alert("Champ manquant:",key.toString())
                    verfied = false;
                }
            }

        })
        if(verfied===true){
            ChargeByAppartement()
            // auThentication.createUserWithEmailAndPassword(userToAdd.email,userToAdd.password).then((Credentials)=>{
            //     fireStore.collection("users").doc(Credentials.user.uid).set(userToAdd).catch((e)=>{
            //         Alert.alert(e.toString());
            //     }).catch((e)=>Alert.alert(e.toString()))
            // }).then(()=>{
            //     Alert.alert(userToAdd.email,"ajoutÃ© ðŸ‘");
            //     setuserToAdd({
            //         email:"",
            //         password:"123456",
            //         nom:"",
            //         prenom:"",
            //         numeroDeTelephone:"",
            //         cin:"",
            //         proprietaireDe:[]
            //     })
            // }).catch((e)=>Alert.alert(e.toString()))
        }
        
        
        
    }
    

    return (
        <TheContextConsumer>
                {(value) => {
                    return(
                        <View style={styles.container}>
                            <Text>ajouter un compte locataire</Text>
                            <ScrollView style={{backgroundColor:"grey",width:"100%",height:"50%",textAlign:"center"}}>
                                
                                    <Text>infos personelles</Text>
                                    
                                    <View>
                                        
                                        {Object.keys(userToAdd).map((key, i)=>{
                                            
                                            if(key !== "proprietaireDe" && key !== "password" ){
                                                return(
                                                    <View key={i}>
                                                        {/* <Text>{key.toString()} </Text> */}
                                                        <TextInput style={styles.input}
                                                            placeholder={key} 
                                                            label={key}
                                                            value={userToAdd[key]}
                                                            onChangeText={(text) => {
                                                                setuserToAdd((prev)=>{
                                                                    let New = {...prev}
                                                                    New[key] = text
                                                                    return(New)
                                                                }) 
                                                            }}
                                                            ref={tag => (myRefs.current[i] = tag)} 
                                                            
                                                            onSubmitEditing={() => {
                                                                
                                                                for(let t = 1 ; t<(myRefs.current.length - i) ; t++ ){
                                                                    if(myRefs.current[i+t]){
                                                                        myRefs.current[i+t].focus();
                                                                        break 
                                                                    }
                                                                    
                                                                }
                                                            }}
                                                        />
                                                    </View>
                                                )        
                                            }
                                            
                                            
                                        })}
                                    </View>
                                    {userToAdd["proprietaireDe"].map((appartement,index) =>{
                                        
                                        return(
                                            <View key={index} style={{backgroundColor:"yellow",textAlign:"center"}}>
                                                <Text > Appartement {index+1} </Text>
                                                    <Button
                                                        title="annuler"
                                                        onPress={()=>{
                                                            
                                                            if(index > -1){
                                                                let n =userToAdd["proprietaireDe"]
                                                                n.splice(index,1)
                                                                setuserToAdd({...userToAdd,proprietaireDe:n})
                                                            }
                                                        }}
                                                    />
                                                

                                                {Object.keys(appartement).map((key, i)=>{
                                                    if(key !== "DateDeSignatureDuContrat"){
                                                        return(
                                                            <View key={i} >
                                                                <Text key={i}>{key} </Text>
                                                                <TextInput
                                                                    ref={tag => (myRefs2.current[i] = tag)} 
                                                                    style={styles.input}
                                                                    placeholder={key}
                                                                    value={appartement[key]}
                                                                    onChangeText={(text) => {
                                                                        let NewObject = {...userToAdd}                                                        
                                                                        userToAdd["proprietaireDe"][index][key] = text;
                                                                        setuserToAdd(NewObject);
                                                                    }}
                                                                    onSubmitEditing={() => {
                                                                
                                                                        for(let t = 1 ; t<(myRefs2.current.length - i) ; t++ ){
                                                                            if(myRefs2.current[i+t]){
                                                                                myRefs2.current[i+t].focus();
                                                                                break 
                                                                            }
                                                                            
                                                                        }
                                                                    }}
                                                                    
                                                                />
                                                        
                                                            </View>
                                                        )   
                                                    }else{
                                                        return(
                                                            
                                                            <TouchableOpacity style={{display:"flex",flexDirection:"row",backgroundColor:"red",alignItems:"center",justifyContent:"center"}} onPress={()=>showDatepicker(index)}>


                                                                <Text style={{width: 300,height: 40,padding: 5,marginVertical: 10,borderColor: 'grey',borderStyle: 'solid',borderRadius: 3,backgroundColor:"white",textAlignVertical:"center",textAlign:"center"}}>
                                                                    {(typeof(appartement[key]) === "string") ? (<Text>date "Click pour selectionner une date" </Text>) : (<Text>{JSON.stringify(appartement[key]).split('"')[1].split("T")[0]}</Text>)}
                                                                </Text>
                                                                
                                                                {(role === index) && (
                                                                
                                                                    <View>
                                                                        
                                                                        <DateTimePicker
                                                                            testID={key.toString()}
                                                                            value={appartement[key] || new Date(1577883600000)}
                                                                            mode={mode}
                                                                            
                                                                            is24Hour={true}
                                                                            display="default"
                                                                            onChange={(event, selectedDate) => {
                                                                                
                                                                                if(selectedDate){
                                                                                    setrole(-1)
                                                                                    setShow(Platform.OS === 'ios');
                                                                                    setuserToAdd(handleDatePickerOnChange(selectedDate,index,key));
                                                                                }else{
                                                                                    setrole(-1)
                                                                                }
                                                                                
                                                                            }
                                                                                
                                                                            }
                                                                        />    
                                                                    </View>
                                                                    
                                                                )}
                                                                
                                                                
                                                            </TouchableOpacity>
                                                        )
                                                    }
                                                    
                                                })}

                                                
                                            </View>
                                        )
                                        
                                    })}
                                    <Button
                                        title="ajouter un appartement"
                                        onPress={() =>{
                                            handleAddAppartement();
                                        }
                                        }
                                    />
                                
                            </ScrollView>
                            
                            <Button
                                title="Ajouter"
                                onPress={handleSubmit}
                            />
                            <View></View> 
                        </View>      
                    )
                }}
        </TheContextConsumer>
            
            

        
