import React from 'react'
import { View, Text,StyleSheet, Button } from 'react-native'
import { TheContextConsumer } from '../context/context'
import { fireStore } from '../firebase/firebase'
export default function Dashboard({ navigation }) {
    
    
    return (
        <TheContextConsumer >
            {(value) => {
                    
                return(
                    <View style={styles.container}>
                        <Text>Dashboard 
                            {value.userState.isAdmin.toString()} 
                        </Text>
                        <View style={{backgroundColor:"yellow",width:"100%",padding:40,display:"flex",flexDirection:"row",justifyContent:"space-between"}}>

                        {(value.userState.isAdmin === true) &&   
                            <View>
                                <Button
                                    title="Ajouter un compte"
                                    onPress={() =>
                                        navigation.navigate('AddUser')
                                    }
                                />
                                <Button
                                    title="Modifier un compte"
                                    onPress={() =>
                                        {
                                            navigation.navigate('SelectUser');
                                            
                                        }
                                    }
                                />
                            </View>
                            
                        }
                            
                            <Button
                                
                                title="Data"
                                onPress={() =>
                                    navigation.navigate('DATA')
                                }
                            />

                        </View>
                        <Button
                            title="Test Date"
                            onPress={() => {
                                fireStore.collection("testDate").get().then((snapshot)=>{
                                    let t = []
                                    snapshot.docs.map((doc)=>{
                                        t.push(doc.data().date.toDate())
                                        console.log(doc.data().date.toDate())
                                    })
                                    // let dif = t[0] - t[1]
                                    console.log((t[0] - t[1])/(1000 * 3600 * 24))
                                })
                                }
                            }
                        />
                        <Button
                            title="Logout"
                            onPress={() => {
                                value.LogOut();
                                navigation.navigate('Home')
                                }
                            }
                        />
                    </View>
                    )
                }
            }
        </TheContextConsumer>
        
    )
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'space-between',
    },
});
