const FindNextJun = (date)=>{
        let dateToSec = date.getTime()
        let junDateToSec = date.setMonth(5)
        junDateToSec = date.setDate(1)
        if(dateToSec-junDateToSec > 0){
            return(new Date(365*24*60*60*1000+junDateToSec))
        }else{
            return(new Date(junDateToSec))
        }
}
const AppCharges = (Appartement) => {
    let Total = 0 ;
    let Charges = [];
    let today = new Date()
    today.setUTCHours(0, 0, 0, 0);
    let NextJunFromToDayTimeStamp = FindNextJun(today).getTime()
    
    if(typeof(Appartement) !== "undefined"){

        let ContractTimeStamp = Appartement.DateDeSignatureDuContrat.getTime()
        let ContractnextJuneTimeStamp = FindNextJun(Appartement.DateDeSignatureDuContrat).getTime()
        do{
            Charges.push({
                days : Math.floor( (ContractnextJuneTimeStamp-ContractTimeStamp)/(1000*60*60*24) ),
                charge : Math.floor( (ContractnextJuneTimeStamp-ContractTimeStamp)/(1000*60*60*24)*(4850/365) ),
                saison : (new Date(ContractnextJuneTimeStamp-(1000*365*60*60*24))).getFullYear()+"/"+(new Date(ContractnextJuneTimeStamp)).getFullYear(),
                startingDay: (JSON.stringify(new Date(ContractTimeStamp))).split('"')[1].split('T')[0],
                To: (JSON.stringify(new Date(ContractnextJuneTimeStamp-(1000*60*60)))).split('"')[1].split('T')[0]
            })
            Total += Math.floor( (ContractnextJuneTimeStamp-ContractTimeStamp)/(1000*60*60*24)*(4850/365) )
            
            ContractTimeStamp = ContractnextJuneTimeStamp;
            ContractnextJuneTimeStamp += 365*24*60*60*1000;   
        }while(ContractnextJuneTimeStamp <= NextJunFromToDayTimeStamp)
    }
    return({Total,Charges})

}
const AllCharges = (ListDesAppartements) => {
    let Appartments = []
    // let ChargesList = []
    let Total = 0
    let today = new Date()
    today.setUTCHours(0, 0, 0, 0);
    let NextJunFromToDayTimeStamp = FindNextJun(today).getTime()
    
    
    if(typeof(ListDesAppartements) !== "undefined"){
        

        ListDesAppartements.map((appartment,i)=>{
            
            let app = {
                titreFoncier : appartment.titreFoncier,
                Immeuble: appartment.Immeuble,
                NAppartement: appartment.NAppartement,
                ChargesList : [],
                Total : 0
            }
            let ContractTimeStamp = appartment.DateDeSignatureDuContrat.toDate().getTime()
            let ContractnextJuneTimeStamp = FindNextJun(appartment.DateDeSignatureDuContrat.toDate()).getTime()

            
            
            do{
                app["ChargesList"].push({
                    days : Math.floor( (ContractnextJuneTimeStamp-ContractTimeStamp)/(1000*60*60*24) ),
                    charge : Math.floor( (ContractnextJuneTimeStamp-ContractTimeStamp)/(1000*60*60*24)*(4850/365) ),
                    saison : (new Date(ContractnextJuneTimeStamp-(1000*365*60*60*24))).getFullYear()+"/"+(new Date(ContractnextJuneTimeStamp)).getFullYear(),
                    startingDay: (JSON.stringify(new Date(ContractTimeStamp))).split('"')[1].split('T')[0],
                    To: (JSON.stringify(new Date(ContractnextJuneTimeStamp-(1000*60*60)))).split('"')[1].split('T')[0]
                })
                app["Total"] += Math.floor( (ContractnextJuneTimeStamp-ContractTimeStamp)/(1000*60*60*24)*(4850/365) )
                Total += Math.floor( (ContractnextJuneTimeStamp-ContractTimeStamp)/(1000*60*60*24)*(4850/365) )

                ContractTimeStamp = ContractnextJuneTimeStamp;
                ContractnextJuneTimeStamp += 365*24*60*60*1000;

            }while(ContractnextJuneTimeStamp <= NextJunFromToDayTimeStamp)
            
            
            Appartments.push(app)
        })
        
    }
    
    
        
    
    return({Appartments,Total})

}
export {AllCharges,AppCharges}
