import React, { useEffect, useRef, useState } from 'react'
import { Alert } from 'react-native'
import { View, Text, TextInput, StyleSheet, Button,ScrollView } from 'react-native'
import { TouchableOpacity } from 'react-native-gesture-handler'
import { TheContextConsumer } from './context/context'
import DateTimePicker from '@react-native-community/datetimepicker';
import { fireStore } from './firebase/firebase'

import _ from 'loadash'

export default function EditUser({ route,navigation }) {
    
    // const [initialUser, setuserToAdd] = useState(route.params.user)
    
    const [userToEdit, setuserToEdit] = useState()

    
    //++++++++++++++++++++++++++++++++++++++++++++++++ 
    useEffect(() => {
        setuserToEdit(route.params.user)
        
    }, [])
    // add appartement to appatements
    const handleAddAppartement = () => {
        setuserToEdit({...userToEdit,...userToEdit.proprietaireDe.push(
            {
                titreFoncier:"",
                DateDeSignatureDuContrat : "",
                NomDuNotaire : "",
                NAppartement : "",
                Immeuble : "",
            }
        )})
    }
    
    // ++++++++++++++++++++++++++++


    // Triggers to show date-pickers
    const [role, setrole] = useState(-1)
    //+++++++++++++++++++++++++++++

    // input Refs To focus on next input automatically
    const myRefs= useRef([]);
    const myRefs2= useRef([]);
    // +++++++++++++++++++++++++++++++++++++++++++++++

    // date picker parameters and functions
    const [mode, setMode] = useState('date');
    const [show, setShow] = useState(false);

    const handleDatePickerOnChange = (selectedDate,index,key) => {

        
        let NewObject = {...userToEdit}
        NewObject["proprietaireDe"][index][key] = (new Date(JSON.stringify(selectedDate).split('"')[1].split('T')[0]));
        return(NewObject)
    }
    const showMode = (currentMode) => {
        setMode(currentMode);
    };
    const showDatepicker = (x) => {
        showMode('date');
        setrole(x)
    };
    // +++++++++++++++++++++++++++++++++++++++++++++++


    
    

    
    
    
    
    const handleSubmit = () => {
        let verfied = true;
        
        if(!userToEdit["proprietaireDe"][0]){
            verfied = false;
            Alert.alert("Champ maquant:","Veuillez ajouter au moins un appartement."+userToEdit["proprietaireDe"].length.toString())
        }
        Object.keys(userToEdit).map((key,i)=>{
            if(key.toString() === "proprietaireDe"){
                
                userToEdit[key].map((appartement,appI)=>{
                    Object.keys(appartement).map((appKey)=>{
                        if(appartement[appKey] === ""){
                            Alert.alert("Champ manquant:", appKey.toString() +"\n"+ "(Appartement : " + (appI+1)+")")
                            verfied = false
                        }
                    })
                })
                
            }else{
                if(userToEdit[key] === ""){
                    Alert.alert("Champ manquant:",key.toString())
                    verfied = false;
                }
            }

        })
        if(verfied===true){

            auThentication.createUserWithEmailAndPassword(userToEdit.email,userToEdit.password).then((Credentials)=>{
                fireStore.collection("users").doc(Credentials.user.uid).set(userToEdit).catch((e)=>{
                    Alert.alert(e.toString());
                }).catch((e)=>Alert.alert(e.toString()))
            }).then(()=>{
                Alert.alert(userToEdit.email,"ajoutÃ© ðŸ‘");
                setuserToEdit({
                    email:"",
                    password:"123456",
                    nom:"",
                    prenom:"",
                    numeroDeTelephone:"",
                    cin:"",
                    proprietaireDe:[]
                })
            }).catch((e)=>Alert.alert(e.toString()))
        }
        
        
        
    }

    return (
        <TheContextConsumer>
                {(value) => {
                    return(
                        <View style={styles.container}>
                            <Text>ajouter un compte locataire</Text>
                            <ScrollView style={{backgroundColor:"grey",width:"100%",height:"50%",textAlign:"center"}}>
                                
                                    <Text>infos personelles</Text>
                                    
                                    <View>
                                        
                                        {(userToEdit) && Object.keys(userToEdit).map((key, i)=>{
                                            
                                            if(key !== "proprietaireDe" && key !== "password" && key !== "id" ){
                                                return(
                                                    <View key={i}>
                                                        {/* <Text>{key.toString()} </Text> */}
                                                        <TextInput style={styles.input}
                                                            placeholder={key} 
                                                            label={key}
                                                            value={userToEdit[key]}
                                                            onChangeText={(text) => {
                                                                setuserToEdit((prev)=>{
                                                                    let New = {...prev}
                                                                    New[key] = text
                                                                    return(New)
                                                                }) 
                                                            }}
                                                            ref={tag => (myRefs.current[i] = tag)} 
                                                            
                                                            onSubmitEditing={() => {
                                                                
                                                                for(let t = 1 ; t<(myRefs.current.length - i) ; t++ ){
                                                                    if(myRefs.current[i+t]){
                                                                        myRefs.current[i+t].focus();
                                                                        break 
                                                                    }
                                                                    
                                                                }
                                                            }}
                                                        />
                                                    </View>
                                                )        
                                            }
                                            
                                            
                                        })}
                                    </View>
                                    {(userToEdit) && userToEdit["proprietaireDe"].map((appartement,index) =>{
                                        
                                        return(
                                            <View key={index} style={{backgroundColor:"yellow",textAlign:"center"}}>
                                                <Text > Appartement {index+1} </Text>
                                                    <Button
                                                        title="annuler"
                                                        onPress={()=>{
                                                            
                                                            if(index > -1){
                                                                let n =userToEdit["proprietaireDe"]
                                                                n.splice(index,1)
                                                                setuserToEdit({...userToEdit,proprietaireDe:n})
                                                            }
                                                        }}
                                                    />
                                                

                                                {Object.keys(appartement).map((key, i)=>{
                                                    if(key !== "DateDeSignatureDuContrat"){
                                                        return(
                                                            <View key={i} >
                                                                <Text key={i}>{key} </Text>
                                                                <TextInput
                                                                    ref={tag => (myRefs2.current[i] = tag)} 
                                                                    style={styles.input}
                                                                    placeholder={key}
                                                                    value={appartement[key]}
                                                                    onChangeText={(text) => {
                                                                        let NewObject = {...userToEdit}                                                        
                                                                        userToEdit["proprietaireDe"][index][key] = text;
                                                                        setuserToEdit(NewObject);
                                                                    }}
                                                                    onSubmitEditing={() => {
                                                                
                                                                        for(let t = 1 ; t<(myRefs2.current.length - i) ; t++ ){
                                                                            if(myRefs2.current[i+t]){
                                                                                myRefs2.current[i+t].focus();
                                                                                break 
                                                                            }
                                                                            
                                                                        }
                                                                    }}
                                                                    
                                                                />
                                                        
                                                            </View>
                                                        )   
                                                    }else{
                                                        return(
                                                            
                                                            <TouchableOpacity style={{display:"flex",flexDirection:"row",backgroundColor:"red",alignItems:"center",justifyContent:"center"}} onPress={()=>showDatepicker(index)}>


                                                                <Text style={{width: 300,height: 40,padding: 5,marginVertical: 10,borderColor: 'grey',borderStyle: 'solid',borderRadius: 3,backgroundColor:"white",textAlignVertical:"center",textAlign:"center"}}>
                                                                    {(typeof(appartement[key]) === "string") ? (<Text>date "Click pour selectionner une date" </Text>) : (<Text>{JSON.stringify(appartement[key]).split('"')[1].split("T")[0]} </Text>)}
                                                                    {/* JSON.stringify(new Date(appartement[key].toDate())).split('"')[1].split('T')[0] */}
                                                                </Text>
                                                                
                                                                {(role === index) && (
                                                                
                                                                    <View>
                                                                        
                                                                        <DateTimePicker
                                                                            testID={key.toString()}
                                                                            value={appartement[key] || new Date(1577883600000)}
                                                                            mode={mode}
                                                                            
                                                                            is24Hour={true}
                                                                            display="default"
                                                                            onChange={(event, selectedDate) => {
                                                                                
                                                                                if(selectedDate){
                                                                                    
                                                                                    setrole(-1)
                                                                                    setShow(Platform.OS === 'ios');
                                                                                    setuserToEdit(handleDatePickerOnChange(selectedDate,index,key));
                                                                                }else{
                                                                                    setrole(-1)
                                                                                }
                                                                                
                                                                            }
                                                                                
                                                                            }
                                                                        />    
                                                                    </View>
                                                                    
                                                                )}
                                                                
                                                                
                                                            </TouchableOpacity>
                                                        )
                                                    }
                                                    
                                                })}

                                                
                                            </View>
                                        )
                                        
                                    })}
                                    <Button
                                        title="ajouter un appartement"
                                        onPress={() =>{
                                            handleAddAppartement();
                                        }
                                        }
                                    />
                                
                            </ScrollView>
                            
                            <Button
                                title="Ajouter"
                                onPress={handleSubmit}
                            />
                            <Button
                                title="Annuler"
                                onPress={()=>{
                                    Alert.alert(JSON.stringify(value.usersList[0].proprietaireDe[0].DateDeSignatureDuContrat),JSON.stringify(userToEdit.proprietaireDe[0].DateDeSignatureDuContrat))
                                    // setuserToAdd(initialUser)
                                    // value.getUsersList()
                                    // Alert.alert(JSON.stringify(route.params.user["proprietaireDe"][0].DateDeSignatureDuContrat),JSON.stringify(userToEdit["proprietaireDe"][0].DateDeSignatureDuContrat))
                                    // navigation.navigate('SelectUser') 
                                }
                                    
                                }
                            />
                            <View></View> 
                        </View>      
                    )
                }}
        </TheContextConsumer>
            
            

        
    )
}


const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'space-between',
    },
    input: {
        width: 300,
        height: 40,
        padding: 5,
        marginVertical: 10,
        marginHorizontal: 30,
        borderWidth: 1,
        borderColor: 'grey',
        borderStyle: 'solid',
        borderRadius: 3,
        backgroundColor:"white",
    },
});
