import React, { useEffect, useState } from 'react'
import { View, Text,StyleSheet, Button, FlatList, ScrollView } from 'react-native'
import { List, ListItem } from "react-native-elements";
import { TheContextConsumer } from '../context/context'
import { fireStore } from '../firebase/firebase'

export default function SelectUser({ navigation }) {
    const [usersList, setusersList] = useState([])

    const getUsersList = () => {
        let t = []
        fireStore.collection("users").get().then((snapshot) => {
            
            snapshot.docs.map((doc) => {
                let x = doc.data();
                x.id = doc.id;
                if(true){
                    t.push(x)
                    console.log(x)
                }
                
            })

        }).then((e)=>{setusersList(t)})
        

         
    }



    useEffect(() => {
        getUsersList()
    }, [])

    return (
        
        <TheContextConsumer >
            {(value) => {
                
                
                return(
                    <View style={styles.container}>
                        <Text>{'SelectUser'} </Text>

                        <Text>select user</Text>
                        
                        <ScrollView style={{backgroundColor:"black",width:"100%"}}>
                            {usersList.map((user,index)=>{

                                return(
                                    <View style={{alignItems:"center",width:"100%",marginVertical:10,backgroundColor:"grey",height:70,display:"flex",flexDirection:"row",justifyContent:"center"}}>

                                            <Button
                                                key={index}
                                                title={user.prenom +" "+ user.nom}
                                                onPress={() =>
                                                    {navigation.navigate('EditUser',{user})}
                                                }
                                                
                                            />     
                                           
                                    </View> 
                                    
                                )
                            })}
                        </ScrollView>


                        {/* <View></View>
                        <View></View>
                        <Text style={{flex:1}}>ttttttttttt</Text> */}

                    </View>
                )
            }}
        </TheContextConsumer>    
    )
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
        // height:"50%",
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'space-between',
    },
});
