import React, { useState } from 'react'
import { Button } from 'react-native'
import { ScrollView } from 'react-native'
import { View, Text,StyleSheet } from 'react-native'

import { TheContextConsumer } from '../context/context'


import AppList from '../components/AppList'

export default function Appartments({navigation}) {
    
    

    return (
        <TheContextConsumer >
            {(value)=>{
                return(
                    <View style={styles.container}>
                        <Text></Text>
                        <View style={{height:70,alignItems:"center",justifyContent:"center",backgroundColor:"lightgrey",width:"100%"}}>
                            <Text> Liste des utilisateurs </Text>
                        </View>
                        <View style={{width:"100%"}}>
                            <View style={styles.row}>
                                
                                <Text style={styles.column}>
                                    {"Imm"}
                                </Text>
                                <Text style={styles.column}>
                                    {"N°App"}
                                </Text>
                                
                                <Text style={styles.column}>
                                    {"à Payer"}
                                </Text>    
                            </View>
                        </View>
                        
                        
                        <ScrollView style={{backgroundColor:"grey",width:"100%",paddingTop:10}}>
                            <AppList Users={value.usersList}/>
                        </ScrollView>
                        
                        <View style={{padding:20}}>
                                <Button
                                    title="Back"
                                    onPress = {()=>{
                                        navigation.navigate("Dashboard")
                                    }}
                                />    
                        </View>

                        
                    </View>            

                )
            }}
        </TheContextConsumer>

        
    )
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'space-between',
    },
    userCard:{
        height:50,
        backgroundColor:"lightgrey",
        padding:10,
        marginVertical:10,
        marginHorizontal:10,
        borderRadius: 10,
        display:"flex",
        flexDirection:"row",
        alignItems:"center",
        justifyContent:"space-between"

    },
    row:{
        // width:"100%",
        height:50,
        padding:10,
        marginHorizontal:10,
        paddingHorizontal:10,
        display:"flex",
        flexDirection:"row",
        justifyContent:"space-between",
        // backgroundColor:"red"
    },
    column :{
        
        fontSize:20,
        textAlign:"center",
        overflow: "hidden",
        flex:1,
        margin:1,
        backgroundColor:"yellow",
        borderWidth: 1,  
        borderColor: '#000',

    },
    text: {  
        color: 'black',  
        marginTop: 10,
        textAlign:"center",
        fontSize:20
    }    
});
