import React, { useRef, useState } from 'react'
import { StyleSheet,View, Text, Button, Alert, ScrollView } from 'react-native'
import { TextInput } from 'react-native-gesture-handler'
import { set } from 'react-native-reanimated';
import { TheContextConsumer } from '../context/context';

export default function AddUser({ navigation }) {
    const [userToAdd, setuserToAdd] = useState({
        email:"",
        password:"",
        nom:"",
        prenom:"",
        numeroDeTelephone:"",
        cin:"",
        proprietaireDe:[]
    })
    const handleAddAppartement = () => {
        setuserToAdd({...userToAdd,...userToAdd.proprietaireDe.push(
            {
                titreFoncier:"",
                DateDeSignatureDuContrat : new Date(0),
                NomDuNotaire : "",
                NAppartement : "",
                Immeuble : "",
            }
        )})
    }

    const ref_input2 = useRef();
    const ref_input3 = useRef();
    const ref_input4 = useRef();
    const ref_input5 = useRef();
    const ref_input6 = useRef();
    
    const handleSubmit = (x) => {
        
        navigation.navigate('Dashboard')
    }

    return (
        <TheContextConsumer>
                {(value) => {
                    return(
                        <View style={styles.container}>
                            <Text>ajouter un compte locataire</Text>
                            <ScrollView style={{backgroundColor:"grey",width:"100%",height:"50%",textAlign:"center"}}>
                                
                                    <Text>infos personelles</Text>
                                    <View>
                                        <TextInput style={styles.input}
                                            placeholder="Email" 
                                            label="Email" 
                                            value={userToAdd.email}
                                            onChangeText={(text) => {
                                                setuserToAdd({...userToAdd,email:text})
                                            }}
                                            autoFocus={true}
                                            onSubmitEditing={() => ref_input2.current.focus()}
                                        />
                                        <TextInput style={styles.input}
                                            placeholder="Password" 
                                            label="Password" 
                                            value={userToAdd.Password}
                                            secureTextEntry={true}
                                            onChangeText={(text) => {
                                                setuserToAdd({...userToAdd,password:text})
                                            }}
                                            ref={ref_input2}
                                            onSubmitEditing={() => ref_input3.current.focus()}
                                        />
                                        <TextInput style={styles.input}
                                            placeholder="Nom" 
                                            label="Nom" 
                                            value={userToAdd.nom}
                                            onChangeText={(text) => {
                                                setuserToAdd({...userToAdd,nom:text})
                                            }}
                                            ref={ref_input3}
                                            onSubmitEditing={() => ref_input4.current.focus()} 
                                        />
                                        <TextInput style={styles.input}
                                            placeholder="Prenom" 
                                            label="Prenom" 
                                            value={userToAdd.prenom}
                                            onChangeText={(text) => {
                                                setuserToAdd({...userToAdd,prenom:text})
                                            }}
                                            ref={ref_input4}
                                            onSubmitEditing={() => ref_input5.current.focus()}
                                        />
                                        <TextInput style={styles.input}
                                            placeholder="Numero De Telephone" 
                                            label="Numero De Telephone" 
                                            value={userToAdd.numeroDeTelephone}
                                            onChangeText={(text) => {
                                                setuserToAdd({...userToAdd,numeroDeTelephone:text})
                                            }}
                                            ref={ref_input5}
                                            onSubmitEditing={() => ref_input6.current.focus()}
                                        />
                                        <TextInput style={styles.input}
                                            placeholder="CIN" 
                                            label="CIN" 
                                            value={userToAdd.cin}
                                            onChangeText={(text) => {
                                                setuserToAdd({...userToAdd,cin:text})
                                            }}
                                            ref={ref_input6}
                                            
                                        />    
                                    </View>
                                    <View>
                                        <Text>{JSON.stringify(userToAdd)} </Text>

                                        <Text>{JSON.stringify(Object.keys(userToAdd))} </Text>

                                        {Object.keys(userToAdd).map((key, i)=>{
                                            if(key !== "proprietaireDe"){
                                                return(

                                                    <View>
                                                        <Text>{userToAdd.email} </Text>
                                                        <Text>{JSON.stringify(userToAdd)} </Text>
                                                        <TextInput style={styles.input}
                                                            placeholder={key} 
                                                            label={key}
                                                            value={userToAdd[key]}
                                                            onChangeText={(text) => {
                                                                setuserToAdd({...userToAdd,email:text})
                                                            }}
                                                            autoFocus={true}
                                                            onSubmitEditing={() => ref_input2.current.focus()}
                                                        />
                                                    </View>
                                                )    
                                            }else{
                                                return(
                                                    <Text>+++++++++++</Text>
                                                )
                                            }
                                            
                                        })}
                                    </View>
                                    {userToAdd["proprietaireDe"].map((appartement,index) =>{
                                        
                                        return(
                                            <View key={index} style={{backgroundColor:"yellow",textAlign:"center"}}>
                                                <Text > Appartement {index+1} </Text>
                                                    <Button
                                                        title="annuler"
                                                        onPress={()=>{
                                                            if(index > -1){
                                                                setuserToAdd({...userToAdd,...userToAdd.proprietaireDe.splice(index, 1)})
                                                            }
                                                        }}
                                                    />
                                                

                                                {Object.keys(appartement).map((key, i)=>{

                                                    return(
                                                        <View key={i} >
                                                            <Text key={i}>{key} </Text>
                                                            <TextInput
                                                                
                                                                style={styles.input}
                                                                placeholder={key}
                                                                value={appartement[key]}
                                                                onChangeText={(text) => {
                                                                    let NemObject = {...userToAdd}                                                        
                                                                    userToAdd["proprietaireDe"][index][key] = text;
                                                                    setuserToAdd(NemObject);
                                                                }} 
                                                                
                                                            />
                                                    
                                                        </View>
                                                    )
                                                })}

                                                
                                            </View>
                                        )
                                        
                                    })}
                                    <Button
                                        title="attribuer un appartement"
                                        onPress={() =>{
                                            handleAddAppartement();
                                        }
                                        }
                                    />
                                
                            </ScrollView>
                            
                            <Button
                                title="Ajouter"
                                onPress={() =>{
                                    handleSubmit(value.addNewUser(userToAdd))
                                }
                                
                                }
                            />
                            <View></View> 
                        </View>      
                    )
                }}
        </TheContextConsumer>
            
            

        
    )
}


const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'space-between',
    },
    input: {
        width: 300,
        height: 40,
        padding: 5,
        marginVertical: 10,
        marginHorizontal: 30,
        borderWidth: 1,
        borderColor: 'grey',
        borderStyle: 'solid',
        borderRadius: 3,
        backgroundColor:"white",
    },
});
