import React from 'react'
import { Button } from 'react-native'
import { View, Text,StyleSheet } from 'react-native'
import { TheContextConsumer } from '../context/context'

export default function Profile({navigation}) {
    return (
        <TheContextConsumer>
                {(value) => {
                    let today = new Date()
                    let nextJuin = today.setMonth(6)
                    let montantAPayer = 0
                    value.userState.proprietaireDe.map((appartement,i)=>{

                        console.log(appartement.DateDeSignatureDuContrat.seconds*1000)
                        console.log((new Date()))
                        let duree = (new Date()).getTime()-appartement.DateDeSignatureDuContrat.seconds

                        console.log(new Date(duree))
                        console.log("Nombre de jours  : "+"\n",duree/(1000*100000*60*60*24))
                        console.log("Nombre de jours  : "+"\n",(today-appartement.DateDeSignatureDuContrat.toDate())/(1000*60*60*24*365),"\n",nextJuin)
                    })
                    

                    return(
                        <View style={styles.container}>
                            <View></View>
                            <Text>Profile</Text>
                            <View>
                                <Button
                                    title="back"
                                    onPress={()=>{
                                        navigation.navigate("Dashboard")
                                    }}
                                />
                            </View>
                        </View>

                    )
                }}
        </TheContextConsumer>        
    )
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
  });
