import React from 'react'
import { Alert } from 'react-native'
import { Button } from 'react-native'
import { View, Text,StyleSheet } from 'react-native'
import { TheContextConsumer } from '../context/context'

export default function Profile({navigation}) {

    const FindNextJun = (date)=>{
        let dateToSec = date.getTime()
        let junDateToSec = date.setMonth(5)
        junDateToSec = date.setDate(1)
        if(dateToSec-junDateToSec > 0){
            return(new Date(365*24*60*60*1000+junDateToSec))
        }else{
            return(new Date(junDateToSec))
        }
    }
    const ChargeByAppartement = (userToAdd) => {
        let newObject = userToAdd
        let montantTotal = 0
        newObject.proprietaireDe.map((Appartement,i) =>{
            
            

            // Alert.alert(
            //     "montant",
            //     JSON.stringify(Math.floor(      ((FindNextJun(today)-Appartement.DateDeSignatureDuContrat)/(1000*60*60*24))*(4850/365)      ))
            //     + " dh "+"\n"+
            //     JSON.stringify(FindNextJun(today))+"\n"+
            //     JSON.stringify(Appartement.DateDeSignatureDuContrat)
            // )  
            
             
        })
        return(

            <View>
                {newObject.proprietaireDe.map((Appartement,i)=>{
                    let today = new Date()
                    today.setUTCHours(0, 0, 0, 0);

                    return(
                        <Text>
                            {"appart : " + (i+1) + "\n"}
                            {"date de signature du contrat : " + JSON.stringify(Appartement.DateDeSignatureDuContrat.toDate()) + "\n"}
                            {JSON.stringify(Math.floor(      ((FindNextJun(today)-Appartement.DateDeSignatureDuContrat)/(1000*60*60*24))*(4850/365)      ))}
                        </Text>
                    )
                })} 
            </View>
        )
    }
    


    

    return (
        <TheContextConsumer>
                {(value) => {

                    
                    return(
                        <View style={styles.container}>
                            <View></View>
                            <Text>Profile</Text>
                            <View>
                                {ChargeByAppartement(value.userState)} 
                                <Text></Text>
                            </View>
                            <View>
                                <Button
                                    title="back"
                                    onPress={()=>{
                                        navigation.navigate("Dashboard")
                                    }}
                                />
                            </View>
                        </View>

                    )
                }}
        </TheContextConsumer>        
    )
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'space-around',
    },
  });
