import React, { createContext, useEffect, useState } from "react";
import { Alert } from "react-native";
import { auThentication, fireStore } from "../firebase/firebase";


const TheContext = createContext();

export default function TheContextProvider(props) {

    const [userState, setuserState] = useState({});
    
    const [usersList, setusersList] = useState([])
    
    
    

    const getUsersList = () => {
        let t = []
        fireStore.collection("users").get().then((snapshot) => {
            
            snapshot.docs.map((doc) => {
                let x = doc.data();
                
                x.proprietaireDe.map((appartement)=>{
                appartement["DateDeSignatureDuContrat"].seconds += 3600

                // Alert.alert( JSON.stringify(appartement["DateDeSignatureDuContrat"].toDate()))
                // appartement["DateDeSignatureDuContrat"] = appartement["DateDeSignatureDuContrat"].toDate()
                })
                
                x.id = doc.id;
                t.push(x)
                // if(!x.isAdmin){
                //     t.push(x)
                //     // console.log(x)
                // }
            })

        }).then((e)=>{setusersList(t)}) 
    }
    
    

    const Login = (email,password) => {
        
        auThentication.signInWithEmailAndPassword(email,password).then((Credentials)=>{
            
            fireStore.collection('users').doc(Credentials.user.uid).get().then((doc)=>{
                setuserState(doc.data());
                getUsersList();
            })
            
        })
    }
    const addNewUser = (userToadd) => {
        // userToadd.password = "******"
        return(
            auThentication.createUserWithEmailAndPassword(userToadd.email,userToadd.password).then((Credentials)=>{
                fireStore.collection("users").doc(Credentials.user.uid).set(userToadd).catch((e)=>{
                    Alert.alert(e.toString());
                }).catch((e)=>Alert.alert(e.toString()))
            }).then(()=>{Alert.alert(userToadd.email,"ajoutÃ© ðŸ‘")}).catch((e)=>Alert.alert(e.toString()))
        )
    }
    
    const LogOut = () =>{
        auThentication.signOut();
        setuserState({
            connected:false,
            isAdmin:false,
            uid : "",
            infos:{
            }
        })
    }

    

    return (
        <TheContext.Provider value={ {userState,usersList,Login,LogOut,addNewUser,  } }>
            {props.children}
        </TheContext.Provider>
    )
}
const TheContextConsumer = TheContext.Consumer;

export {TheContextProvider,TheContextConsumer}
