import React, { useRef, useState } from 'react'
import { StyleSheet,View, Text, Button, Alert, ScrollView } from 'react-native'
import { TextInput } from 'react-native-gesture-handler'
import { set } from 'react-native-reanimated';
import { TheContextConsumer } from '../context/context';

export default function AddUser({ navigation }) {
    const [userToAdd, setuserToAdd] = useState({
        email:"",
        password:"123456",
        nom:"",
        prenom:"",
        numeroDeTelephone:"",
        cin:"",
        proprietaireDe:[]
    })
    const handleAddAppartement = () => {
        setuserToAdd({...userToAdd,...userToAdd.proprietaireDe.push(
            {
                titreFoncier:"",
                DateDeSignatureDuContrat : new Date(0),
                NomDuNotaire : "",
                NAppartement : "",
                Immeuble : "",
            }
        )})
    }
    const myRefs= useRef([]);

    const filter = (object) => {

        let NewObject = object;
        delete NewObject["proprietaireDe"]
        return(NewObject)
    }
    
    const handleSubmit = (x) => {
        
        navigation.navigate('Dashboard')
    }

    return (
        <TheContextConsumer>
                {(value) => {
                    return(
                        <View style={styles.container}>
                            <Text>ajouter un compte locataire</Text>
                            <ScrollView style={{backgroundColor:"grey",width:"100%",height:"50%",textAlign:"center"}}>
                                
                                    <Text>infos personelles</Text>
                                    
                                    <View>
                                        {console.log(filter(userToAdd))}
                                        {Object.keys(filter(userToAdd)).map((key, i)=>{
                                            
                                            if(key !== "proprietaireDe" && key !== "password" ){
                                                return(
                                                    <View key={i}>
                                                        <Text>{key.toString()} </Text>
                                                        <TextInput style={styles.input}
                                                            placeholder={key} 
                                                            label={key}
                                                            value={userToAdd[key]}
                                                            onChangeText={(text) => {
                                                                setuserToAdd((prev)=>{
                                                                    let New = {...prev}
                                                                    New[key] = text
                                                                    return(New)
                                                                }) 
                                                            }}
                                                            ref={tag => (myRefs.current[i] = tag)} 
                                                            
                                                            onSubmitEditing={() => {
                                                                if(myRefs.current[i+1]){
                                                                    myRefs.current[i+1].focus()  
                                                                }else if(myRefs.current[i+2]){
                                                                    myRefs.current[i+2].focus()
                                                                }
                                                                
                                                            }}
                                                        />
                                                    </View>
                                                )        
                                            }
                                            
                                            
                                        })}
                                    </View>
                                    {userToAdd["proprietaireDe"].map((appartement,index) =>{
                                        
                                        return(
                                            <View key={index} style={{backgroundColor:"yellow",textAlign:"center"}}>
                                                <Text > Appartement {index+1} </Text>
                                                    <Button
                                                        title="annuler"
                                                        onPress={()=>{
                                                            if(index > -1){
                                                                setuserToAdd({...userToAdd,...userToAdd.proprietaireDe.splice(index, 1)})
                                                            }
                                                        }}
                                                    />
                                                

                                                {Object.keys(appartement).map((key, i)=>{

                                                    return(
                                                        <View key={i} >
                                                            <Text key={i}>{key} </Text>
                                                            <TextInput
                                                                
                                                                style={styles.input}
                                                                placeholder={key}
                                                                value={appartement[key]}
                                                                onChangeText={(text) => {
                                                                    let NemObject = {...userToAdd}                                                        
                                                                    userToAdd["proprietaireDe"][index][key] = text;
                                                                    setuserToAdd(NemObject);
                                                                }} 
                                                                
                                                            />
                                                    
                                                        </View>
                                                    )
                                                })}

                                                
                                            </View>
                                        )
                                        
                                    })}
                                    <Button
                                        title="attribuer un appartement"
                                        onPress={() =>{
                                            handleAddAppartement();
                                        }
                                        }
                                    />
                                
                            </ScrollView>
                            
                            <Button
                                title="Ajouter"
                                onPress={() =>{
                                    console.log(userToAdd,myRefs)
                                    // handleSubmit(value.addNewUser(userToAdd))
                                }
                                
                                }
                            />
                            <View></View> 
                        </View>      
                    )
                }}
        </TheContextConsumer>
            
            

        
    )
}


const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'space-between',
    },
    input: {
        width: 300,
        height: 40,
        padding: 5,
        marginVertical: 10,
        marginHorizontal: 30,
        borderWidth: 1,
        borderColor: 'grey',
        borderStyle: 'solid',
        borderRadius: 3,
        backgroundColor:"white",
    },
});

