import React, { useRef, useState } from 'react'
import { StyleSheet,View, Text, Button, Alert, ScrollView } from 'react-native'
import { TextInput, TouchableOpacity } from 'react-native-gesture-handler'

import { TheContextConsumer } from '../context/context';
import DateTimePicker from '@react-native-community/datetimepicker';
export default function AddUser({ navigation }) {
    const [userToAdd, setuserToAdd] = useState({
        email:"",
        password:"123456",
        nom:"",
        prenom:"",
        numeroDeTelephone:"",
        cin:"",
        proprietaireDe:[]
    })
    const handleAddAppartement = () => {
        setuserToAdd({...userToAdd,...userToAdd.proprietaireDe.push(
            {
                titreFoncier:"",
                DateDeSignatureDuContrat : "",
                NomDuNotaire : "",
                NAppartement : "",
                Immeuble : "",
            }
        )})
    }
    const myRefs= useRef([]);
    // ++++++++++++++++++++++++++++
    const [date, setDate] = useState(new Date(1598051730000));
    const [mode, setMode] = useState('date');
    const [show, setShow] = useState(false);
    const handleOnChange = (event, selectedDate) => {
        const currentDate = selectedDate || date;
        setShow(Platform.OS === 'ios');
        setDate(currentDate);
    };

    const showMode = (currentMode) => {
    setShow(true);
    setMode(currentMode);
    };
    const showDatepicker = () => {
        showMode('date');
    };
    
    
    const handleSubmit = (x) => {
        
        navigation.navigate('Dashboard')
    }

    return (
        <TheContextConsumer>
                {(value) => {
                    return(
                        <View style={styles.container}>
                            <Text>ajouter un compte locataire</Text>
                            <ScrollView style={{backgroundColor:"grey",width:"100%",height:"50%",textAlign:"center"}}>
                                
                                    <Text>infos personelles</Text>
                                    
                                    <View>
                                        
                                        {Object.keys(userToAdd).map((key, i)=>{
                                            
                                            if(key !== "proprietaireDe" && key !== "password" ){
                                                return(
                                                    <View key={i}>
                                                        <Text>{key.toString()} </Text>
                                                        <TextInput style={styles.input}
                                                            placeholder={key} 
                                                            label={key}
                                                            value={userToAdd[key]}
                                                            onChangeText={(text) => {
                                                                setuserToAdd((prev)=>{
                                                                    let New = {...prev}
                                                                    New[key] = text
                                                                    return(New)
                                                                }) 
                                                            }}
                                                            ref={tag => (myRefs.current[i] = tag)} 
                                                            
                                                            onSubmitEditing={() => {
                                                                
                                                                for(let t = 1 ; t<(myRefs.current.length - i) ; t++ ){
                                                                    if(myRefs.current[i+t]){
                                                                        myRefs.current[i+t].focus();
                                                                        break 
                                                                    }
                                                                    
                                                                }
                                                            }}
                                                        />
                                                    </View>
                                                )        
                                            }
                                            
                                            
                                        })}
                                    </View>
                                    {userToAdd["proprietaireDe"].map((appartement,index) =>{
                                        
                                        return(
                                            <View key={index} style={{backgroundColor:"yellow",textAlign:"center"}}>
                                                <Text > Appartement {index+1} </Text>
                                                    <Button
                                                        title="annuler"
                                                        onPress={()=>{
                                                            if(index > -1){
                                                                setuserToAdd({...userToAdd,...userToAdd.proprietaireDe.splice(index, 1)})
                                                            }
                                                        }}
                                                    />
                                                

                                                {Object.keys(appartement).map((key, i)=>{
                                                    if(key !== "DateDeSignatureDuContrat"){
                                                        return(
                                                            <View key={i} >
                                                                <Text key={i}>{key} </Text>
                                                                <TextInput
                                                                    
                                                                    style={styles.input}
                                                                    placeholder={key}
                                                                    value={appartement[key]}
                                                                    onChangeText={(text) => {
                                                                        let NemObject = {...userToAdd}                                                        
                                                                        userToAdd["proprietaireDe"][index][key] = text;
                                                                        setuserToAdd(NemObject);
                                                                    }} 
                                                                    
                                                                />
                                                        
                                                            </View>
                                                        )   
                                                    }else{
                                                        return(
                                                            
                                                            <TouchableOpacity style={{display:"flex",flexDirection:"row",backgroundColor:"red",alignItems:"center",justifyContent:"center"}} onPress={showDatepicker}>
                                                                <Text
                                                                    style={{width: 250,height: 40,padding: 5,marginVertical: 10,borderColor: 'grey',borderStyle: 'solid',borderRadius: 3,backgroundColor:"white"}}
                                                                    
                                                                    // placeholder={key}
                                                                    // value={appartement[key].toString()}
                                                                    
                                                                >{appartement[key]} {JSON.stringify(date.getFullYear())+" / "+JSON.stringify(date.getMonth())+" / "+JSON.stringify(date.getDay())}  </Text>
                                                                <View style={{height: 40,width:40,justifyContent:"center"}}>
                                                                    <Button title="+" onPress={()=>{Alert.alert("test")}} />
                                                                </View>
                                                                {show && (
                                                                    <DateTimePicker
                                                                        testID="dateTimePicker"
                                                                        value={date}
                                                                        mode={mode}
                                                                        is24Hour={true}
                                                                        display="default"
                                                                        onChange={handleOnChange}
                                                                    />
                                                                )}
                                                                
                                                                
                                                            </TouchableOpacity>
                                                        )
                                                    }
                                                    
                                                })}

                                                
                                            </View>
                                        )
                                        
                                    })}
                                    <Button
                                        title="attribuer un appartement"
                                        onPress={() =>{
                                            handleAddAppartement();
                                        }
                                        }
                                    />
                                
                            </ScrollView>
                            
                            <Button
                                title="Ajouter"
                                onPress={() =>{
                                    console.log(userToAdd,myRefs)
                                    // handleSubmit(value.addNewUser(userToAdd))
                                }
                                
                                }
                            />
                            <View></View> 
                        </View>      
                    )
                }}
        </TheContextConsumer>
            
            

        
    )
}


const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'space-between',
    },
    input: {
        width: 300,
        height: 40,
        padding: 5,
        marginVertical: 10,
        marginHorizontal: 30,
        borderWidth: 1,
        borderColor: 'grey',
        borderStyle: 'solid',
        borderRadius: 3,
        backgroundColor:"white",
    },
});

